$(document).ready(function(){$('.navbar-nav [data-toggle="tooltip"]').tooltip();$(".navbar-twitch-toggle").on("click",function(a){a.preventDefault();$(".navbar-twitch").toggleClass("open")});$(".nav-style-toggle").on("click",function(b){b.preventDefault();var a=$(".nav-style-toggle.disabled");$(this).addClass("disabled");a.removeClass("disabled");$(".navbar-twitch").removeClass("navbar-"+a.data("type"));$(".navbar-twitch").addClass("navbar-"+$(this).data("type"))})});$(".addtocart").click(function(){var a=$("#quantity-"+$(this).data("productid"));if(a.val()>0){$.ajax({type:"POST",url:"/assets/scripts/addtocart.php",data:"iProductID="+$(this).data("productid")+"&iQuantity="+a.val(),success:function(b){console.log(b);var c=$.parseJSON(b);$("#product"+c.iProductID+" .isincart").text("Dette produkt ligger i kurven");$("#productsInCart").text(c.productsInCart)}})}else{a[0].setCustomValidity("Antallet skal være større end 0");a[0].reportValidity()}});$(".remove").click(function(){$.ajax({type:"POST",url:"/assets/scripts/removeproduct.php",data:"iProductID="+$(this).data("productid"),success:function(a){var b=$.parseJSON(a);$("#product"+b.productId).remove();location.reload()}})});$(".update").click(function(){var a=$("#quantity-"+$(this).data("id"));if(a.val()>0){$.ajax({type:"POST",url:"/assets/scripts/updateQuantity.php",data:"cartlineid="+$(this).data("id")+"&quantity="+a.val(),success:function(b){console.log(b);var d=$.parseJSON(b);if(d.status){$("#cartline"+d.cartlineid+" .isincart").text("Antal er rettet");$("#productsInCart").text(d.productsInCart);var c=$("#cartline"+d.cartlineid+" .price").text();c=parseFloat(c);pricesum=c*a.val();$("#cartline"+d.cartlineid+" .linetotal").text(pricesum+",-");$(".total-sum").text(d.cartTotal+" "+d.priceFormat)}}})}else{a[0].setCustomValidity("Antallet skal være større end 0");a[0].reportValidity()}});$(".search").click(function(){console.log(11);$.ajax({type:"POST",url:"/assets/scripts/search.php",success:function(a){}})});